int* findAnagrams(char* s, char* p, int* returnSize)
{
    int hash[26] = { 0 };
    int hash1[26] = { 0 };
    int sz = strlen(s);
    int len = strlen(p);
    for (int i = 0; i < len; i++)
    {
        hash1[p[i] - 'a']++;
    }
    int* ret = (int*)malloc(sizeof(int) * sz);
    for (int i = 0; i <= sz - len; i++)
    {
        memset(hash, 0, sizeof(hash));
        for (int j = i; j < i + len; j++)
        {
            hash[s[j] - 'a']++;
        }
        int k = 0;
        for (k = 0; k < 26; k++)
        {
            if (hash[k] != hash1[k])
                break;
        }
        if (k == 26)
        {
            ret[(*returnSize)++] = i;
        }
    }
    return ret;
}
int main()
{
    char s[] = "abab";
    char p[] = "ab";
    int sz = 0;
    int* ret = findAnagrams(s, p, &sz);
    for (int i = 0; i < sz; i++)
    {
        printf("%d ", ret[i]);
    }
    return 0;
}
